create table users(id integer auto_increment primary key, username char(50) not null, realname char(200) not null, email char(100) not null, mobile char(50) not null, weixin char(200) not null, sina char(200) not null, renren char(200) not null, password char(250) not null, role tinyint(4) not null, integral mediumint(10) not null, referSource char(255) not null, referType char(255) not null);